mapper.select:
select article_no, subject, user_id, content, hit, recommendation, comment, date, isnotice
from board
where article_no = #{articleNo}

mapper.update:
update board
set subject = #{subject}, content = #{content}
where article_no = #{articleNo}

update board
set hit = hit + 1
where article_no = #{articleNo}

update board
set comment = comment + 1
where article_no = #{articleNo}

update board
set comment = comment - 1
where article_no = #{articleNo}
