mapper.select:
select content_id, user_mytrip_no, no, user_id
from mytrip 
where user_id = #{userId} 
and user_mytrip_no = #{userMytripNo}

select count(user_mytrip_no) user_mytrip_no
from mytrip 
where user_id = #{userId}

mapper.delete:
delete 
from mytrip
where user_id = #{userId} 
and user_mytrip_no = #{userMytripNo}
